mapper.select:
SELECT
*,(SELECT name FROM t_agent where t_agent.id=t_settlement.agent_id)agent_name
FROM t_settlement



                AND INSTR(t_settlement.agent_id,#{queryForm.agentId})



        AND DATE_FORMAT(t_settlement.startdate, '%Y-%m-%d') >= #{startDate}




        AND DATE_FORMAT(t_settlement.enddate, '%Y-%m-%d') >= #{endDate}




        AND DATE_FORMAT(t_settlement.cktime, '%Y-%m-%d') >= #{check_startDate}




        AND DATE_FORMAT(t_settlement.cktime, '%Y-%m-%d') >= #{check_endDate}

mapper.delete:
delete from t_settlement
where settlement_no= #{settlementNo}
and cktime is null

mapper.update:
update t_settlement set
    ck_name= #{ckName}
    cktime= now()
where settlement_no= #{settlementNo}
and cktime is null
