mapper.select:
SELECT
*
FROM t_settlement



                AND INSTR(t_settlement.agent_id,#{queryForm.agentId})



        AND DATE_FORMAT(t_settlement.start date, '%Y-%m-%d') >= #{queryForm.startDateBegin}


        AND DATE_FORMAT(t_settlement.start date, '%Y-%m-%d') <= #{queryForm.startDateEnd}



        AND DATE_FORMAT(t_settlement.end date, '%Y-%m-%d') >= #{queryForm.endDateBegin}


        AND DATE_FORMAT(t_settlement.end date, '%Y-%m-%d') <= #{queryForm.endDateEnd}
